export interface TSRadioGroupHTMLAttributes {
	/**  Value of currently chosen ts-radio node  */
	value?: string;

	/**  Title of radio group  */
	title?: string;

	/**  Index of checked ts-radio node  */
	index?: number;

	/**  Is group currently focused for keyboard input  */
	focused?: boolean;

	/**  Direction 'rtl' or 'ltr'  */
	dir?: string;

}

